package io.github.jangalinski.aoc._2022

fun main() {
  fun String.findFirstMarker(): Int {
    val x = windowed(14, step = 1)
    val y = x.mapIndexed { i,w -> i to  (w.toSet().size == 14)  }.filter { it.second }

    return y.first().first +14
  }

